import React, { useState } from "react";
import "./membership.css";
import { useNavigate } from "react-router-dom";

import searchIcon from "../components/assets/search.png";
import filterIcon from "../components/assets/filter.png";
import glass2Icon from "../components/assets/glass2.png"; 
import membershipIcon from "../components/assets/membership.png"; // Update icon path

// Sample membership data
const data = Array.from({ length: 15 }, (_, index) => ({
  select: index % 3 === 0, // Some rows selected
  id: 20 + index,
  membership: "Gold Member",
  description: "Valid for VIP Customers Only",
  active: true,
  membershipType: index % 4 === 0 ? "Auto Earned Based Program" : 
                  index % 4 === 1 ? "Purchase Based Program" :
                  index % 4 === 2 ? "Game Played Earned Based Program" : 
                  "Season Pass",
  allowBonusPoints: true,
  allowTimePlay: true,
  discountPercentage: "15%",
  pointsMultiplier: "2x",
  lastUpdatedDate: "09-Dec-2024 12:11",
  lastUpdatedUser: "Mubin"
}));

const HeaderCell = ({ label }) => (
  <div className="membership-header-container">
    <span className="membership-header-text">{label}</span>
    <div className="membership-sort-icons">
      <span className="membership-arrow membership-up1"></span>
      <span className="membership-arrow membership-down1"></span>
    </div>
  </div>
);

const MembershipScreen = () => {
  const [activeMembershipsOnly, setActiveMembershipsOnly] = useState(false);
  const [membershipFilter, setMembershipFilter] = useState("");
  const [showFilterDropdown, setShowFilterDropdown] = useState(false);
  const [selectedRow, setSelectedRow] = useState(null);
  const navigate = useNavigate();

  const handleCheckboxChange = (index) => {
    setSelectedRow(index);
  };

  const handleUpdate = () => {
    if (selectedRow !== null) {
      console.log("Update row:", data[selectedRow]);
      // Navigate to update form or open modal
      navigate(`/membership-update/${data[selectedRow].id}`);
    }
  };

  return (
    <div className="membership-table-wrapper">
      {/* ---------- Section Header ---------- */}
      <div className="membership-section-header">
        <div className="membership-section-title-container">
          <img src={membershipIcon} alt="Membership Icon" className="membership-section-icon" /> 
          <h2 className="membership-section-title">Membership</h2>

          <div className="membership-checkbox-container">
            <input
              type="checkbox"
              id="activeOnly"
              checked={activeMembershipsOnly}
              onChange={(e) => setActiveMembershipsOnly(e.target.checked)}
            />
            <label htmlFor="activeOnly" className="membership-checkbox-label22">
              Active Memberships Only
            </label>
          </div>
        </div>
      </div>

      {/* ---------- Action Row ---------- */}
      <div className="membership-header-actions">
        {/* Search box */}
        <div className="membership-search-container2">
          <img src={searchIcon} alt="Search" className="membership-search-input2-icon" />
          <input
            type="text"
            placeholder="Search"
            value={membershipFilter}
            onChange={(e) => setMembershipFilter(e.target.value)}
            className="membership-search-input2"
          />
        </div>

        {/* Filter Button */}
        <div className="membership-filter-container">
          <button 
            className="membership-filter-btn"
            onClick={() => setShowFilterDropdown(!showFilterDropdown)}
          >
            <img src={filterIcon} alt="Filter" className="membership-filter-icon" />
          </button>
          {showFilterDropdown && (
            <div className="membership-filter-dropdown">
              <div className="membership-filter-option">Membership</div>
              <div className="membership-filter-option">Type</div>
              <div className="membership-filter-option">Status</div>
            </div>
          )}
        </div>

        {/* Blue Search Button */}
        <button className="membership-btn-search">
          <img src={glass2Icon} alt="Search" className="membership-search-btn-icon" />
          Search
        </button>

        {/* Update + Add + Refresh */}
        <div className="membership-action-buttons">
          <button
            className="membership-btn-update"
            onClick={handleUpdate}
          >
            Update
          </button>
          <button 
            className="membership-btn-add"
            onClick={() => navigate("/membership-add")}
          >
            Add
          </button>
          <button className="membership-btn-refresh">Refresh</button>
        </div>
      </div>

      {/* Table */}
      <div className="membership-table-scroll">
        <table className="membership-custom-table">
          <thead>
            <tr>
              <th><HeaderCell label="Select" /></th>
              <th><HeaderCell label="Id" /></th>
              <th><HeaderCell label="Membership" /></th>
              <th><HeaderCell label="Description" /></th>
              <th><HeaderCell label="Active" /></th>
              <th><HeaderCell label="Membership Type" /></th>
              <th><HeaderCell label="Allow Bonus Points" /></th>
              <th><HeaderCell label="Allow Time Play" /></th>
              <th><HeaderCell label="Discount Percentage" /></th>
              <th><HeaderCell label="Points Multiplier" /></th>
              <th><HeaderCell label="Last Updated Date" /></th>
              <th><HeaderCell label="Last Updated User" /></th>
            </tr>
          </thead>
          <tbody>
            {data.length === 0 ? (
              <tr>
                <td colSpan="12" style={{textAlign: 'center', padding: '40px', color: '#999'}}>
                  No data available
                </td>
              </tr>
            ) : (
              data.map((row, index) => (
                <tr key={index}>
                  <td>
                    <input
                      type="checkbox"
                      checked={selectedRow === index}
                      onChange={() => handleCheckboxChange(index)}
                    />
                  </td>
                  <td>{row.id}</td>
                  <td>{row.membership}</td>
                  <td>{row.description}</td>
                  <td>
                    <input 
                      type="checkbox" 
                      checked={row.active} 
                      readOnly 
                      className="membership-active-checkbox" 
                    />
                  </td>
                  <td>
                    <div className="membership-type-box">{row.membershipType}</div>
                  </td>
                  <td>
                    <input 
                      type="checkbox" 
                      checked={row.allowBonusPoints} 
                      readOnly 
                      className="membership-active-checkbox" 
                    />
                  </td>
                  <td>
                    <input 
                      type="checkbox" 
                      checked={row.allowTimePlay} 
                      readOnly 
                      className="membership-active-checkbox" 
                    />
                  </td>
                  <td>{row.discountPercentage}</td>
                  <td>{row.pointsMultiplier}</td>
                  <td>{row.lastUpdatedDate}</td>
                  <td>{row.lastUpdatedUser}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default MembershipScreen;
