import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
import './userroleadd.css';
import cancelIcon from '../../components/assets/iconoir_cancel.png'; 
import userroleIcon from "../../components/assets/userroleIcon.png";

const UserRoleSetup = () => {
  const [selectedApp, setSelectedApp] = useState('Back Office System');
  const [userRole, setUserRole] = useState('System Administrator');
  const [description, setDescription] = useState('Redemption');

  // Application items with their subcontent
  const applicationData = {
    'POS Software': [],
    'Back Office System': [
      'User Management',
      'Role Configuration', 
      'System Settings',
      'Database Management',
      'Backup & Recovery',
      'Audit Logs'
    ],
    'Product Definitions': [
      'Product Categories',
      'Product Attributes',
      'Pricing Rules',
      'Inventory Management'
    ],
    'Security': [
      'Access Control',
      'Authentication',
      'Permissions',
      'Security Policies'
    ],
    'Memberships/Rewards': [
      'Membership Tiers',
      'Reward Programs',
      'Points System',
      'Loyalty Cards'
    ],
    'Block Access Profile': [],
    'Game settings': [
      'Game Parameters',
      'Difficulty Levels',
      'Scoring System'
    ],
    'Location Table': [
      'Store Locations',
      'Regional Settings',
      'Timezone Configuration'
    ]
  };

  const descriptionItems = [
    'Add ERP Points',
    'Add/Edit Cash Card Definition',
    'Add/Edit Corp Redemption Monetary Value',
    'Add/Edit Gate Passes Definition',
    'Add/Edit General Merchandise Product Definition',
    'Add/Edit Corp Package Deal Definition',
    'Add/Edit Gate Promo codes Definition',
    'Add/Edit Promotions Definition'
  ];

  const handleAppSelection = (appName) => {
    setSelectedApp(appName);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-container">
        {/* Header */}
        <div className="modal-header">
          <div className="header-content">
            <div className="star-icon">
              <svg viewBox="0 0 24 24" fill="currentColor">
                <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z"/>
              </svg>
            </div>
            <h2 className="modal-title">User Role Setup</h2>
          </div>
          <button className="close-button">
            <svg viewBox="0 0 24 24" fill="currentColor">
              <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"/>
            </svg>
          </button>
        </div>

        <div className="modal-body">
          {/* General Settings Section */}
          <div className="section">
            <div className="section-header">
              <h3>General Settings</h3>
            </div>
            <div className="general-settings-content">
              {/* First row: ID and User Role */}
              <div className="form-row">
                <div className="form-group id-group">
                  <label>ID</label>
                  <input
                    type="text"
                    value="120"
                    className="form-input id-input"
                    readOnly
                  />
                </div>
                <div className="form-group user-role-group">
                  <label>User Role</label>
                  <input
                    type="text"
                    value={userRole}
                    onChange={(e) => setUserRole(e.target.value)}
                    className="form-input user-role-input"
                  />
                </div>
              </div>

              {/* Second row: Description */}
              <div className="form-row">
                <div className="form-group description-group">
                  <label>Description</label>
                  <input
                    type="text"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    className="form-input description-input"
                  />
                </div>
              </div>
            </div>
          </div>

          {/* Application and Description Sections */}
          <div className="two-column-layout">
            {/* Application Section */}
            <div className="section">
              <div className="section-header">
                <h3>Application</h3>
              </div>
              <div className="section-content">
                <div className="scrollable-list">
                  {Object.keys(applicationData).map((appName, index) => (
                    <div key={index}>
                      {/* Main application item */}
                      <div
                        className={`list-item ${
                          appName === selectedApp ? 'selected' : ''
                        } ${appName === 'Product Definitions' ? 'outlined' : ''}`}
                        onClick={() => handleAppSelection(appName)}
                      >
                        {appName}
                      </div>

                      {/* Subcontent - only show if this app is selected and has subcontent */}
                      {appName === selectedApp && applicationData[appName].length > 0 && (
                        <div className="subcontent">
                          {applicationData[appName].map((subItem, subIndex) => (
                            <div key={subIndex} className="subcontent-item">
                              <div className="subcontent-bullet">•</div>
                              <span>{subItem}</span>
                            </div>
                          ))}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>
            </div>

            {/* Description Section */}
            <div className="section">
              <div className="section-header">
                <h3>Description</h3>
              </div>
              <div className="section-content">
                <div className="scrollable-list">
                  {descriptionItems.map((item, index) => (
                    <div key={index} className="description-item">
                      <div className="checkbox-icon">
                        <svg viewBox="0 0 20 20" fill="currentColor">
                          <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd"/>
                        </svg>
                      </div>
                      <span>{item}</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="button-group">
            <button className="btn btn-save">Save</button>
            <button className="btn btn-okay">Okay</button>
            <button className="btn btn-cancel">Cancel</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserRoleSetup;
