import React, { useState } from "react";
import "./membership.css";
import { useNavigate } from "react-router-dom";

import searchIcon from "../components/assets/search.png";
import membershipIcon from "../components/assets/accessprofile.png"; // You'll need to add this icon

// Sample membership data
const membershipData = Array.from({ length: 12 }, (_, index) => ({
  select: false,
  active: true,
  id: [23, 45, 36, 78, 45, 11, 76, 78, 56, 78, 56, 78][index] || 23,
  membership: "Gold Member",
  description: "Valid for VIP Customers Only",
  membershipType: [
    "Auto Earned Based Program",
    "Purchase Based Program", 
    "Game Played Earned Based Program",
    "Season Pass",
    "Activity"
  ][index % 5]
}));

const HeaderCell = ({ label }) => (
  <div className="membership-header-container">
    <span className="membership-header-text">{label}</span>
    <div className="membership-sort-icons">
      <span className="membership-arrow membership-up1"></span>
      <span className="membership-arrow membership-down1"></span>
    </div>
  </div>
);

const Membership = () => {
  const [activeOnly, setActiveOnly] = useState(false);
  const [searchFilter, setSearchFilter] = useState("");
  const [selectedRows, setSelectedRows] = useState(new Set());
  const navigate = useNavigate();

  const handleCheckboxChange = (index) => {
    const newSelectedRows = new Set(selectedRows);
    if (newSelectedRows.has(index)) {
      newSelectedRows.delete(index);
    } else {
      newSelectedRows.add(index);
    }
    setSelectedRows(newSelectedRows);
  };

  const handleSelectAll = (checked) => {
    if (checked) {
      setSelectedRows(new Set(membershipData.map((_, index) => index)));
    } else {
      setSelectedRows(new Set());
    }
  };

  const filteredData = membershipData.filter(item => {
    const matchesSearch = searchFilter === "" || 
      item.membership.toLowerCase().includes(searchFilter.toLowerCase()) ||
      item.description.toLowerCase().includes(searchFilter.toLowerCase()) ||
      item.membershipType.toLowerCase().includes(searchFilter.toLowerCase());

    const matchesActive = !activeOnly || item.active;

    return matchesSearch && matchesActive;
  });

  return (
    <div className="membership-table-wrapper">
      {/* ---------- Section Header ---------- */}
      <div className="membership-section-header">
        <div className="membership-section-title-container">
          <div className="membership-back-icon">
            <span>‚Üê</span>
          </div>
          <img src={membershipIcon} alt="Membership Icon" className="membership-section-icon" />
          <h2 className="membership-section-title">Membership</h2>
        </div>
      </div>

      {/* ---------- Action Row ---------- */}
      <div className="membership-header-actions">
        {/* Search box */}
        <div className="membership-search-container">
          <img src={searchIcon} alt="Search" className="membership-search-input-icon" />
          <input
            type="text"
            placeholder="Search"
            value={searchFilter}
            onChange={(e) => setSearchFilter(e.target.value)}
            className="membership-search-input"
          />
        </div>

        {/* Action buttons */}
        <div className="membership-action-buttons">
          <button className="membership-btn-add">Add</button>
          <button className="membership-btn-save">Save</button>
          <button className="membership-btn-refresh">Refresh</button>
        </div>
      </div>

      {/* Table */}
      <div className="membership-table-scroll">
        <table className="membership-custom-table">
          <thead>
            <tr>
              <th>
                <input
                  type="checkbox"
                  onChange={(e) => handleSelectAll(e.target.checked)}
                  checked={selectedRows.size === membershipData.length}
                />
              </th>
              <th><HeaderCell label="Active" /></th>
              <th><HeaderCell label="Id" /></th>
              <th><HeaderCell label="Membership" /></th>
              <th><HeaderCell label="Description" /></th>
              <th><HeaderCell label="Membership Type" /></th>
            </tr>
          </thead>
          <tbody>
            {filteredData.length === 0 ? (
              <tr>
                <td colSpan="6" style={{textAlign: 'center', padding: '40px', color: '#999'}}>
                  No data available
                </td>
              </tr>
            ) : (
              filteredData.map((row, index) => (
                <tr key={index}>
                  <td>
                    <input
                      type="checkbox"
                      checked={selectedRows.has(index)}
                      onChange={() => handleCheckboxChange(index)}
                    />
                  </td>
                  <td>
                    <input 
                      type="checkbox" 
                      checked={row.active} 
                      readOnly 
                      className="membership-active-checkbox" 
                    />
                  </td>
                  <td>{row.id}</td>
                  <td>{row.membership}</td>
                  <td>{row.description}</td>
                  <td>{row.membershipType}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Membership;
